mapper.select:
select
from epidemic_prevention where 1 = 1
and `type` = #{type, jdbcType=VARCHAR}
and title like concat('%',#{title, jdbcType=VARCHAR},'%')
order by createTime desc limit #{page.start, jdbcType=INTEGER}, #{page.pageSize, jdbcType=INTEGER}

select count(1) from epidemic_prevention where 1 = 1

  and `type` = #{type, jdbcType=VARCHAR}


  and title like concat('%',#{title, jdbcType=VARCHAR},'%')
